import Layout from "../components/layout";
import axios from "axios";
import { useState } from "react";

const edit = props => {
  const [state, setState] = useState("");
  const [status, setStatuts] = useState("");

  var onUpdateHandler = e => {
    axios.patch("http://localhost:5000/tasks/" + e.target.id + "/status");
  };

  var onDeleteHandler = e => {
    axios.delete("http://localhost:5000/tasks/" + e.target.id);
  };

  var onChangeHandler = e => {
    setState({ ...state, [e.target.name]: e.target.value });
    console.log(state);
  };

  var addTaskHandler = e => {
    // fetch("http://localhost:5000/tasks", state)
    //   .then(res => console.log(res.data))
    //   .catch(err => console.log(err));
    axios
      .post("http://localhost:5000/tasks", state)
      .then(res => console.log(res.data))
      .catch(err => console.log(err));
  };

  console.log(props.allTasks);

  var allTasks = props.allTasks.map(task => {
    return (
      <div key={task._id} className="card" style={{ margin: "16px" }}>
        <p>Title : {task.title}</p>
        <p>Description : {task.description}</p>
        <p>Status : {task.status} </p>
        <button id={task._id}>Update</button>
        <button id={task._id} onClick={e => onDeleteHandler(e)}>
          Delete
        </button>
      </div>
    );
  });

  return (
    <Layout>
      Welcome to edit Page
      {allTasks}
      <label>Title</label>
      <input
        type="text"
        name="title"
        onChange={e => onChangeHandler(e)}
      ></input>
      <label>Description</label>
      <input
        type="text"
        name="description"
        onChange={e => onChangeHandler(e)}
      ></input>
      <button onClick={addTaskHandler}>Add Task</button>
    </Layout>
  );
};

edit.getInitialProps = async function() {
  const res = await axios.get("http://localhost:5000/tasks");

  return {
    allTasks: res.data
  };
};

export default edit;
