import Layout from "../components/layout";
import axios from "axios";

const edit = () => {
  return <Layout>Welcome to edit Page</Layout>;
};

index.getInitialProps = async function() {
  const res = await axios.get("http://localhost:5000/tasks");

  return {
    allTasks: res.data
  };
};

export default edit;
